    @GET
    @Path("/pdf-stream4/{documentId}/{secretKey}")
    @Produces("application/pdf")
    //@Produces("text/html;charset=UTF-8")
    //@Produces(MediaType.TEXT_PLAIN)
    public Response getFilePDFStream4(@PathParam("documentId") String documentId, @PathParam("secretKey") String secretKey) {
        System.out.println("in DocumentResource getFilePDF()");
        try {
            if ("abc".equals(secretKey)) {
                System.out.println("secretKey is valid");
                String fileContents = "";
                //return Response.ok(fileContents, "application/pdf").build();
                Base64.Decoder decoder = Base64.getDecoder();
                
                // https://stackoverflow.com/questions/19556034/jax-rs-could-not-find-messagebodywriter-for-response-object-of-type
                // By default there's only a limited list of supported types that resteasy can automatically marshal and unmarshal
                
                InputStream is = new ByteArrayInputStream(decoder.decode(fileContents));
                
                return Response.ok(is).build();
                //return Response.ok(new GenericEntity<StreamedContent>(media)).build();
            } else {
                throw new Exception(String.format("invalid secretKey: %s", secretKey));
            }
        } catch (Exception e) {
            return Response.status(Response.Status.BAD_REQUEST).entity(e).build();
        }
    }
    
