    @GET
    @Path("/docx/{documentId}/{secretKey}")
    @Produces("application/vnd.openxmlformats-officedocument.wordprocessingml.document")
    public Response getFileDOCX(@PathParam("documentId") String documentId, @PathParam("secretKey") String secretKey) {
        System.out.println("in DocumentResource getFileDOCX()");
        try {
            if ("abc".equals(secretKey)) {
                System.out.println("secretKey is valid");
                String fileContents = "";
                //return Response.ok(fileContents, "image/jpg").build();
                Base64.Decoder decoder = Base64.getDecoder();
                ResponseBuilder response = Response.ok((Object) decoder.decode(fileContents), "application/vnd.openxmlformats-officedocument.wordprocessingml.document");
                response.header("Content-Disposition", "attachment; filename=test.docx");
                return response.build();
            } else {
                throw new Exception(String.format("invalid secretKey: %s", secretKey));
            }
        } catch (Exception e) {
            return Response.status(Response.Status.BAD_REQUEST).entity(e).build();
        }
    }

